CREATE TABLE announcements ( announcement_id int(10) unsigned NOT NULL AUTO_INCREMENT, registration_type enum('DEGREE','PRE-ADMISSION','GUEST') NOT NULL, concentration_id int(10) unsigned NOT NULL, course_term_id int(10) unsigned NOT NULL, announcement text, PRIMARY KEY (announcement_id) ) ENGINE=InnoDB DEFAULT CHARSET=latin1;
CREATE TABLE category ( concentration_id int(10) unsigned NOT NULL, category_id int(10) unsigned NOT NULL, category_code varchar(10) NOT NULL, category_label varchar(64) NOT NULL, PRIMARY KEY (category_id) ) ENGINE=InnoDB DEFAULT CHARSET=latin1;
CREATE TABLE category_req ( concentration_id int(10) unsigned NOT NULL, category_id int(10) unsigned NOT NULL, category_count int(10) unsigned NOT NULL, admission tinyint(1) NOT NULL, thesis tinyint(1) NOT NULL, capstone tinyint(1) NOT NULL ) ENGINE=InnoDB DEFAULT CHARSET=latin1;
CREATE TABLE concentration ( concentration_id int(10) unsigned NOT NULL, concentration_code varchar(10) NOT NULL, concentration_label varchar(64) NOT NULL, PRIMARY KEY (concentration_id) ) ENGINE=InnoDB DEFAULT CHARSET=latin1;
CREATE TABLE concentration_req ( concentration_id int(10) unsigned NOT NULL, num_courses int(10) unsigned NOT NULL, thesis tinyint(1) NOT NULL, capstone tinyint(1) NOT NULL ) ENGINE=InnoDB DEFAULT CHARSET=latin1;
CREATE TABLE course ( concentration_id int(10) unsigned NOT NULL, course_id int(10) unsigned NOT NULL, course_code varchar(64) NOT NULL, course_title varchar(255) NOT NULL, course_term_id int(10) unsigned NOT NULL, course_day set('MON','TUES','WED','THUR','FRI','SAT','SUN'), course_time varchar(64) NOT NULL, course_type varchar(64) NOT NULL, course_limit int(3) unsigned, instructor varchar(64) NOT NULL, location varchar(64), attributes set('TMF','DCOM','DC','SP','SD','WAD','WP','SRM','DM','MGMT','AL','P','AC','LA','CD','DIGM','EID','WDD','CAP','E') NOT NULL, PRIMARY KEY (course_id) ) ENGINE=InnoDB DEFAULT CHARSET=latin1;
CREATE TABLE course_terms ( course_term_id int(10) unsigned NOT NULL, course_term_label varchar(64) NOT NULL, current_course tinyint(1) NOT NULL, PRIMARY KEY (course_term_id) ) ENGINE=InnoDB DEFAULT CHARSET=latin1;
CREATE TABLE grades ( grade_id int(10) unsigned NOT NULL, grade_label varchar(64) NOT NULL, gpa decimal(3,2) NOT NULL, PRIMARY KEY (grade_id) ) ENGINE=InnoDB DEFAULT CHARSET=latin1;
CREATE TABLE mobile_auth_token ( user_id int(10) unsigned NOT NULL, auth_token varchar(255) NOT NULL, device_type varchar(32) NOT NULL, device_os varchar(32) NOT NULL, create_date timestamp DEFAULT CURRENT_TIMESTAMP, modify_date datetime DEFAULT '0000-00-00 00:00:00' NOT NULL, PRIMARY KEY (user_id, device_type, device_os) ) ENGINE=InnoDB DEFAULT CHARSET=latin1;
CREATE TABLE users ( user_id int(10) unsigned NOT NULL AUTO_INCREMENT, username varchar(255) NOT NULL, password_hash varchar(255) NOT NULL, email varchar(255) NOT NULL, registration_type enum('DEGREE','PRE-ADMISSION','GUEST') NOT NULL, concentration_id int(10) unsigned NOT NULL, gpa decimal(3,2), current_login datetime, last_login datetime, create_date timestamp DEFAULT CURRENT_TIMESTAMP, modify_date datetime DEFAULT '0000-00-00 00:00:00' NOT NULL, PRIMARY KEY (user_id) ) ENGINE=InnoDB DEFAULT CHARSET=latin1;
CREATE TABLE users_courses ( user_id int(10) unsigned NOT NULL, course_id int(10) unsigned NOT NULL, grade_id int(10) unsigned NOT NULL, PRIMARY KEY (user_id, course_id) ) ENGINE=InnoDB DEFAULT CHARSET=latin1;
